import axios from 'axios';

const apiUrl = "http://localhost:5224"; // מחובר ל-API

export default {
  // שליפת כל המשימות
  getTasks: async () => {
    try {
      const result = await axios.get(`${apiUrl}/items`);
      return result.data;
    } catch (error) {
      console.error('Error fetching tasks:', error);
      return [];
    }
  },

  // הוספת משימה חדשה
  addTask: async (name) => {
    console.log('Adding task:', name);
    try {
      const result = await axios.post(`${apiUrl}/items`, { name, isComplete: false });
      return result.data;
    } catch (error) {
      console.error('Error adding task:', error);
      return null;
    }
  },

  // עדכון סטטוס משימה
  setCompleted: async (id, isComplete) => {
    console.log('Updating task:', { id, isComplete });
    try {
      await axios.put(`${apiUrl}/items/${id}`, { isComplete });
    } catch (error) {
      console.error('Error updating task:', error);
    }
  },

  // מחיקת משימה לפי ID
  deleteTask: async (id) => {
    console.log('Deleting task:', id);
    try {
      await axios.delete(`${apiUrl}/items/${id}`);
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  }
};
