import axios from 'axios';

// הגדרת כתובת ה-API כ-default לכל קריאות ה-axios
// const apiUrl = "https://localhost:5224";

// הגדרת axios עם config default
axios.defaults.baseURL = apiUrl;
axios.defaults.headers.post['Content-Type'] = 'application/json';

// הוספת interceptor לתפיסת שגיאות ב-response
axios.interceptors.response.use(
  response => response,  // במקרה של הצלחה, נחזיר את התשובה
  error => {
    // במקרה של שגיאה, נרשום את השגיאה ללוג
    console.error('API Error:', error.response ? error.response.data : error.message);
    return Promise.reject(error);  // נחזיר את השגיאה כך שנוכל לטפל בה במקומות אחרים
  }
);

export default {
  // 1. שליפת כל המשימות
  getTasks: async () => {
    try {
      const result = await axios.get('/items');
      return result.data;
    } catch (error) {
      console.error("Error fetching tasks:", error);
      throw error;
    }
  },

  // 2. הוספת משימה חדשה
  addTask: async (name) => {
    try {
      const result = await axios.post('/items', { name, isComplete: false });
      console.log("Task added:", result.data);
      return result.data;
    } catch (error) {
      console.error("Error adding task:", error);
      throw error;
    }
  },

  // 3. עדכון סטטוס משימה (השלמה/לא הושלמה)
  setCompleted: async (id, isComplete) => {
    try {
      const result = await axios.put(`/items/${id}`, { isComplete });
      console.log("Task updated:", result.data);
      return result.data;
    } catch (error) {
      console.error("Error updating task:", error);
      throw error;
    }
  },

  // 4. מחיקת משימה
  deleteTask: async (id) => {
    try {
      await axios.delete(`/items/${id}`);
      console.log("Task deleted:", id);
      return id;
    } catch (error) {
      console.error("Error deleting task:", error);
      throw error;
    }
  }
};
